@include('components.my-nav')

<div class="items-center justify-center w-full transition-opacity opacity-100 duration-1500 lg:grow starting:opacity-0">


    <div class="container mx-auto px-4 sm:px-6 lg:px-8 py-8 md:py-12">
    
        {{-- Status Message (from session after join/leave action) --}}
        @if (session('status'))
            <div class="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded relative mb-6 shadow" role="alert">
                <strong class="font-bold">Başarılı!</strong>
                <span class="block sm:inline">{{ session('status') }}</span>
            </div>
        @endif
         @if (session('error'))
            <div class="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-6 shadow" role="alert">
                <strong class="font-bold">Hata!</strong>
                <span class="block sm:inline">{{ session('error') }}</span>
            </div>
        @endif
    
        {{-- Main Boycott Details Card --}}
        <div class="bg-white dark:bg-gray-800 shadow-xl rounded-lg overflow-hidden">
            <div class="p-6 md:p-10">
    
                {{-- Boycott Title (Brand Name) --}}
                <h1 class="text-3xl md:text-4xl font-bold text-gray-900 dark:text-white mb-6 border-b border-gray-200 dark:border-gray-700 pb-4">
                    Boykot: {{ $boycott->brand->name }}
                </h1>
    
                {{-- Grid for Details --}}
                <div class="grid grid-cols-1 md:grid-cols-3 gap-8 mb-8">
                    {{-- Left Column (Brand Info, Dates) --}}
                    <div class="md:col-span-1 space-y-4">
                        <div>
                            <h2 class="text-lg font-semibold text-gray-800 dark:text-gray-200 mb-1">Marka Bilgileri</h2>
                            <p class="text-gray-600 dark:text-gray-400">
                                <span class="font-medium text-gray-700 dark:text-gray-300">Marka:</span> {{ $boycott->brand->name }}
                            </p>
                            @if($boycott->brand->alternatives)
                            <p class="text-gray-600 dark:text-gray-400 mt-1">
                                 <span class="font-medium text-gray-700 dark:text-gray-300">Alternatifler:</span> {{ $boycott->brand->alternatives }}
                            </p>
                            @endif
